// Import React and Component
import React, {useState, createRef} from 'react';
import {CameraKitCameraScreen} from 'react-native-camera-kit';
import {
  StyleSheet,
  TextInput,
  View,
  Text,
  Image,
  KeyboardAvoidingView,
  TouchableOpacity,
  ScrollView,
  SafeAreaView,
  PermissionsAndroid,
  Alert,
  Platform,
} from 'react-native';

import Loader from './Components/Loader';

const RegisterScreen = ({navigation}) => {
  const [userName, setUserName] = useState('');
  const [phone, setPhone] = useState('');
  const [BVN, setBVN] = useState('');
  const [imageStr, setImageStr] = useState('');
  const [cameraActive, SetCameraActive] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errortext, setErrortext] = useState('');
  const [isRegistraionSuccess, setIsRegistraionSuccess] = useState(false);

  const nameInputRef = createRef();
  const phoneInputRef = createRef();
  const BvnInputRef = createRef();

  const [isPermitted, setIsPermitted] = useState(false);
  const [captureImages, setCaptureImages] = useState([]);

  const requestCameraPermission = async () => {
    try {
      const granted = await PermissionsAndroid.request(
        PermissionsAndroid.PERMISSIONS.CAMERA,
        {
          title: 'Camera Permission',
          message: 'App needs camera permission',
        },
      );
      // If CAMERA Permission is granted
      return granted === PermissionsAndroid.RESULTS.GRANTED;
    } catch (err) {
      console.warn(err);
      return false;
    }
  };

  const requestExternalWritePermission = async () => {
    try {
      const granted = await PermissionsAndroid.request(
        PermissionsAndroid.PERMISSIONS.WRITE_EXTERNAL_STORAGE,
        {
          title: 'External Storage Write Permission',
          message: 'App needs write permission',
        },
      );
      // If WRITE_EXTERNAL_STORAGE Permission is granted
      return granted === PermissionsAndroid.RESULTS.GRANTED;
    } catch (err) {
      console.warn(err);
      alert('Write permission err', err);
    }
    return false;
  };

  const requestExternalReadPermission = async () => {
    try {
      const granted = await PermissionsAndroid.request(
        PermissionsAndroid.PERMISSIONS.READ_EXTERNAL_STORAGE,
        {
          title: 'Read Storage Permission',
          message: 'App needs Read Storage Permission',
        },
      );
      // If READ_EXTERNAL_STORAGE Permission is granted
      return granted === PermissionsAndroid.RESULTS.GRANTED;
    } catch (err) {
      console.warn(err);
      alert('Read permission err', err);
    }
    return false;
  };

  const openCamera = async () => {
    if (Platform.OS === 'android') {
      if (await requestCameraPermission()) {
        if (await requestExternalWritePermission()) {
          if (await requestExternalReadPermission()) {
            setIsPermitted(true);
            SetCameraActive(true);
          } else {
            alert('READ_EXTERNAL_STORAGE permission denied');
          }
        } else {
          alert('WRITE_EXTERNAL_STORAGE permission denied');
        }
      } else {
        alert('CAMERA permission denied');
      }
    } else {
      setIsPermitted(true);
      SetCameraActive(true);
    }
  };

  const onBottomButtonPressed = event => {
    const images = JSON.stringify(event.captureImages);
    console.log(images);
    if (event.type === 'left') {
      setIsPermitted(false);
    } else if (event.type === 'right') {
      setIsPermitted(false);
      setCaptureImages(images);
    } else {
      SetCameraActive(false);
      Alert.alert(
        event.type,
        images,
        [{text: 'OK', onPress: () => console.log('OK Pressed')}],
        {cancelable: false},
      );
    }
    SetCameraActive(false);
  };

  const handleCamera = () => {
    SetCameraActive(!cameraActive);
  };
  const handleSubmitButton = () => {
    setErrortext('');
    if (!userName) {
      alert('Please fill Name');
      return;
    }

    if (!phone) {
      alert('Please enter phone number');
      return;
    }
    if (!BVN) {
      alert('Please fill BVN');
      return;
    }
    // if (!imageStr) {
    //   alert('Please click your picture and submit');
    //   return;
    // }
    //Show Loader

    console.log('values are', userName, phone, BVN);
    let imageTopass =
      '';

    setLoading(true);

    fetch('http://52.172.149.74:5001/register', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        Name: userName,
        Phone_Number: phone,
        BVN: BVN,
        Image: imageTopass,
      }),
    })
      .then(response => response.json())
      .then(responseJson => {
        //Hide Loader
        setLoading(false);
        console.log(responseJson);
        // If server response message same as Data Matched
        if (responseJson.status === 1) {
          setIsRegistraionSuccess(true);

          console.log('Registration Successful. Please Login to proceed');
        } else {
          setErrortext('Registration Unsuccessful');
        }
      })
      .catch(error => {
        //Hide Loader
        setLoading(false);
        console.error(error);
      });
  };
  if (isRegistraionSuccess) {
    return (
      <View
        style={{
          flex: 1,
          backgroundColor: '#307ecc',
          justifyContent: 'center',
        }}>
        <Image
          source={require('../Image/success.png')}
          style={{height: 150, resizeMode: 'contain', alignSelf: 'center'}}
        />
        <Text style={styles.successTextStyle}>Registration Successful.</Text>
        <TouchableOpacity
          style={styles.buttonStyle}
          activeOpacity={0.5}
          onPress={() => navigation.navigate('LoginScreen')}>
          <Text style={styles.buttonTextStyle}>Login Now</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <View style={styles.mainBody}>
      <Loader loading={loading} />
      {cameraActive ? (
        <SafeAreaView style={{flex: 1}}>
          <View style={{flex: 1}}>
            <CameraKitCameraScreen
              // Buttons to perform action done and cancel
              actions={{
                rightButtonText: 'Done',
                leftButtonText: 'Cancel',
              }}
              onBottomButtonPressed={event => onBottomButtonPressed(event)}
              captureButtonImage={require('./../assets/capture.png')}
            />
          </View>
        </SafeAreaView>
      ) : (
        <ScrollView
          keyboardShouldPersistTaps="handled"
          contentContainerStyle={{
            justifyContent: 'center',
            alignContent: 'center',
          }}>
          <KeyboardAvoidingView enabled>
            <Text style={styles.registerTextStyle}>Please enter details</Text>
            <View style={styles.SectionStyle}>
              <TextInput
                style={styles.inputStyle}
                onChangeText={UserName => setUserName(UserName)}
                underlineColorAndroid="#f000"
                placeholder="Enter First Name"
                placeholderTextColor="#8b9cb5"
                autoCapitalize="sentences"
                returnKeyType="next"
                ref={nameInputRef}
                onSubmitEditing={() =>
                  nameInputRef.current && nameInputRef.current.focus()
                }
                blurOnSubmit={false}
              />
            </View>
            {/* <View style={styles.SectionStyle}>
            <TextInput
              style={styles.inputStyle}
              onChangeText={(UserEmail) => setUserEmail(UserEmail)}
              underlineColorAndroid="#f000"
              placeholder="Enter Last Name"
              placeholderTextColor="#8b9cb5"
              //keyboardType="email-address"
              ref={emailInputRef}
              returnKeyType="next"
              onSubmitEditing={() =>
                emailInputRef.current && emailInputRef.current.focus()
              }
              blurOnSubmit={false}
            />
          </View> */}
            <View style={styles.SectionStyle}>
              <TextInput
                style={styles.inputStyle}
                onChangeText={phone => setPhone(phone)}
                underlineColorAndroid="#f000"
                placeholder="Enter Phone number"
                placeholderTextColor="#8b9cb5"
                keyboardType="numeric"
                ref={phoneInputRef}
                returnKeyType="next"
                onSubmitEditing={() =>
                  phoneInputRef.current && phoneInputRef.current.focus()
                }
                blurOnSubmit={false}
              />
            </View>

            <View style={styles.SectionStyle}>
              <TextInput
                style={styles.inputStyle}
                onChangeText={BVN => setBVN(BVN)}
                underlineColorAndroid="#f000"
                placeholder="Enter BVN"
                placeholderTextColor="#8b9cb5"
                keyboardType="numeric"
                ref={BvnInputRef}
                returnKeyType="next"
                onSubmitEditing={() =>
                  BvnInputRef.current && BvnInputRef.current.focus()
                }
                blurOnSubmit={false}
              />
            </View>

            <TouchableOpacity
              style={styles.buttonStyle}
              activeOpacity={0.5}
              onPress={openCamera}>
              <Text style={styles.buttonTextStyle}>
                Upload your profile Image
              </Text>
            </TouchableOpacity>

            {errortext !== '' ? (
              <Text style={styles.errorTextStyle}> {errortext} </Text>
            ) : null}
            <TouchableOpacity
              style={styles.buttonStyle}
              activeOpacity={0.5}
              onPress={handleSubmitButton}>
              <Text style={styles.buttonTextStyle}>REGISTER</Text>
            </TouchableOpacity>
          </KeyboardAvoidingView>
        </ScrollView>
      )}
    </View>
  );
};

export default RegisterScreen;

const styles = StyleSheet.create({
  SectionStyle: {
    flexDirection: 'row',
    height: 40,
    marginTop: 20,
    marginLeft: 35,
    marginRight: 35,
    margin: 10,
  },
  mainBody: {
    flex: 1,
    justifyContent: 'center',
    backgroundColor: '#307ecc',
    alignContent: 'center',
  },
  buttonStyle: {
    backgroundColor: '#7DE24E',
    borderWidth: 0,
    color: '#FFFFFF',
    borderColor: '#7DE24E',
    height: 40,
    alignItems: 'center',
    borderRadius: 30,
    marginLeft: 35,
    marginRight: 35,
    marginTop: 20,
    marginBottom: 20,
  },
  buttonTextStyle: {
    color: '#FFFFFF',
    paddingVertical: 10,
    fontSize: 16,
  },
  inputStyle: {
    flex: 1,
    color: 'white',
    paddingLeft: 15,
    paddingRight: 15,
    borderWidth: 1,
    borderRadius: 30,
    borderColor: '#dadae8',
  },
  registerTextStyle: {
    color: '#FFFFFF',
    textAlign: 'center',
    //fontWeight: 'bold',
    fontSize: 14,
    alignSelf: 'center',
    padding: 10,
  },
  errorTextStyle: {
    color: 'red',
    textAlign: 'center',
    fontSize: 14,
  },
  successTextStyle: {
    color: 'white',
    textAlign: 'center',
    fontSize: 18,
    padding: 30,
  },
});
